// 무한루프 : for(;;) , while(true)
public class 자바반복문4 {
    // char => 연산 처리 => int
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // A~Z
		for(char c='A';c<='Z';c++)
			System.out.print(c);
		System.out.println();
		// a~z
		for(char c='a';c<='z';c++)
			System.out.print(c);
	}

}
